import React, { Component } from "react";

class EditUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      name: "",
      phone: "",
      permission: ""
    };
  }

  onChangeValueInputAdd = event => {
    var name = event.target.name;
    var value = event.target.value;
    this.setState({
      [name]: value
    });
  };
  /**Add */

  render() {
    //console.log(this.props.Edit);
    const {dataEdit} = this.props.Edit;
    return (
      <div className="blue">
        <form className="ui form">
          <a className="ui label">Edit user</a>

          <div className="field">
            <label>Name</label>
            <input
            defaultValue={dataEdit.name}
              name="name"
              onChange={event => {
                this.onChangeValueInputAdd(event);
              }}
              placeholder="name..."
            />
          </div>
          <div className="field">
            <label>Phone</label>
            <input
             defaultValue={dataEdit.phone}
              name="phone"
              onChange={event => {
                this.onChangeValueInputAdd(event);
              }}
              placeholder="number phone"
            />
          </div>
          <div className="field">
            <label>Right</label>
            <select
              onChange={event => {
                this.onChangeValueInputAdd(event);
              }}
              name="permission"
            >
              <option value={1}>Emloyee</option>
              <option value={-1}>Client</option>
            </select>
          </div>
          <button type="reset" className="ui button">
            Save
          </button>
        </form>
      </div>
    );
  }
}

export default EditUser;
