import React, { Component } from "react";
import "./App.css";
import HeaderTop from "./component/HeaderTop";
import Search from "./component/Search";
import TableData from "./component/TableData";
import AddUser from "./component/AddUser";
import dataUser from "./data.json";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: dataUser,
      dataSeek: ""
    };
  }
  DataSearch = data => {
    this.setState({
      dataSeek: data
    });
    console.log("TCL: App -> DataSearch -> data", this.state.dataSeek);
  };

  render() {
    console.log("TCL: App -> constructor -> data", this.state.data);
    var dataChange = [];
    this.state.data.forEach(item => {
      if (item.name.indexOf(this.state.dataSeek) !== -1) {
         dataChange.push(item)
      }
    });
    return (
      <div>
        <HeaderTop />
        <div className="ui vertically divided grid">
          <div className="allcolumn">
            <div className="column1">
              <Search DataSearch={data => this.DataSearch(data)} />
              <TableData DataUser={this.state.data} />
            </div>
            <div className="column2">
              <AddUser />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default App;
