import React, { Component } from "react";

class AddUser extends Component {
  constructor(props) {
    super(props);
    this.state = {
      id: "",
      name: "",
      phone: "",
      permisstion: ""
    };
  }

  onChangeValueInputAdd = event => {
    var name = event.target.name;
    var value = event.target.value;
    this.setState({
      [name]: value
    });
    //dong goi item chuyen ra app.js
    var itemNew = {};
    itemNew.id = this.state.id;
    itemNew.name = this.state.name;
    itemNew.phone = this.state.phone;
    itemNew.permission = this.state.permission;
    console.log(itemNew);
  };
  /**Add */
  AddItem = (event) => {
    event.preventDefaulf;
    this.props.AddItem(this.state);
  };

  render() {
    //console.log(this.state);
    return (
      <div>
        <a className="ui label">Add new personel</a>
        <form className="ui form">
          <div className="field">
            <label>Name</label>
            <input
              name="name"
              onChange={event => {
                this.onChangeValueInputAdd(event);
              }}
              placeholder="name..."
            />
          </div>
          <div className="field">
            <label>Phone</label>
            <input
              name="phone"
              onChange={event => {
                this.onChangeValueInputAdd(event);
              }}
              placeholder="number phone"
            />
          </div>
          <div className="field">
            <label>Right</label>
            <select
              onChange={event => {
                this.onChangeValueInputAdd(event);
              }}
              name="permission"
            >
              <option value={1}>Emloyee</option>
              <option value={2}>Client</option>
            </select>
          </div>
          <button
            onClick={data => {
              this.AddItem(data);
            }}
            type="submit"
            className="ui button"
          >
            Add
          </button>
        </form>
      </div>
    );
  }
}

export default AddUser;
